docReady(()=>{"use strict";var e=document;const t=e.querySelector("#clientslist");let n;null!==e.querySelector('meta[name="csrf-token"]')&&(n=e.querySelector('meta[name="csrf-token"]').content);const o={"X-Requested-With":"XMLHttpRequest",cache:"no-cache","Cache-Control":"no-store, no-transform, max-age=0, private","Content-Type":"application/json"};null!==n&&(o["X-CSRF-TOKEN"]=n),window.refreshClients=function(e){fetch("/api/clients/"+e,{headers:o}).then(e=>e.text()).then(e=>{t.innerHTML=e})},setTimeout(refreshClients,1e3,1)});