docReady(()=>{"use strict";var e=document.querySelector(".grid");const r=document.querySelector(".filters"),a=document.querySelector(".ordering");let s=new Isotope(e,{itemSelector:".grid-item",layoutMode:"fitRows",sortBy:"product",sortAscending:!0}),c=e=>{for(const t of e.getElementsByTagName("a"))t.classList.remove("active")};null!==r&&r.addEventListener("click",e=>{e.preventDefault();var e=e.target,t=e.dataset.filter;c(r),e.classList.add("active"),s.arrange({filter:t})}),null!==a&&a.addEventListener("click",e=>{e.preventDefault();var e=e.target,t="true"===e.dataset.ordering;c(a),e.classList.add("active"),s.arrange({sortAscending:t})})});